import styles from './banner_module.scss'; // Import SCSS module

function Banner({ title, subtitles, mainText }) { // Accept props for dynamic content
    return (
        <div className={styles.hero}>
            <section className={styles.heroContent}>
                <h2 className={styles.srOnly}>{title || 'Promoted Content'}</h2>
                {subtitles.map((subtitle, index) => ( // Dynamically generate subtitles
                    <p key={index} className={styles.subtitle}>{subtitle}</p>
                ))}
                <p className={styles.text}>{mainText || 'Open a savings account with Argent Bank today!'}</p>
            </section>
        </div>
    );
}

export default Banner;
