// Importer la fonction `configureStore` depuis Redux Toolkit pour configurer le store Redux
import { configureStore } from "@reduxjs/toolkit";
// Importer le réducteur `userReducer` depuis le dossier `reducers`
import userReducer from './reducers';

// Créer et configurer le store Redux en utilisant configureStore
export const store = configureStore({
  // Définir les réducteurs pour gérer les différentes parties de l'état de l'application
  reducer: {
    user: userReducer, // Utiliser le réducteur `userReducer` pour gérer l'état de l'utilisateur
  },
});
