// Import des styles SCSS pour le composant Header
import './header_module.scss';

// Import de l'image du logo
import Logo from "../../assets/argentBankLogo.webp";

// Import des composants et des fonctions nécessaires depuis React et React Router
import { Link, NavLink, useNavigate } from 'react-router-dom';
import '../../utils/font-awesome-4.7.0/scss/font-awesome.scss';

// Import des hooks React
import { useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';

// Import des actions et sélecteurs Redux depuis le slice de l'utilisateur
import { fetchUserBytoken, userSelector, clearState } from '../../features/UserSlices';

// Composant Header
function Header() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const { isError, username, firstName } = useSelector(userSelector);

  // Effet pour gérer l'authentification de l'utilisateur et la récupération des données
  useEffect(() => {
    if (token) {
      dispatch(fetchUserBytoken({ token }));
    }

    // Effet de nettoyage à la déconnexion
    return () => {
      dispatch(clearState());
    };
  }, [token, dispatch]);

  // Effet pour gérer les erreurs
  useEffect(() => {
    if (isError) {
      dispatch(clearState());
    }
  }, [isError, dispatch]);

  // Fonction de déconnexion
  const logout = () => {
    localStorage.removeItem("token");
    navigate("/signIn");
  };

  // Fonction pour rendre les liens de navigation en fonction de l'état d'authentification
  const renderMenuLinks = () => {
    if (token) {
      return (
        <>
          <NavLink activeClassName='active' className="main-nav-item" to={'/user'}>
            <i className="fa fa-user-circle"></i>
            {username || firstName}
          </NavLink>
          <Link className="main-nav-item" onClick={logout}>
            <i className="fa fa-sign-out"></i>
            Sign Out
          </Link>
        </>
      );
    } else {
      return (
        <NavLink activeClassName='active' className="main-nav-item" to={'/signIn'}>
          <i className="fa fa-user-circle"></i>
          Sign In
        </NavLink>
      );
    }
  };

  return (
    // Barre de navigation principale
    <nav className="main-nav">
      {/* Logo et lien vers la page d'accueil */}
      <Link className="main-nav-logo" to={'/'}>
        <img className="main-nav-logo-image" src={Logo} alt="Argent Bank Logo" />
        <h1 className="sr-only">Argent Bank</h1>
      </Link>
      <div>
        {/* Affichage des liens de navigation en fonction de l'authentification */}
        {renderMenuLinks()}
      </div>
    </nav>
  );
}

export default Header;
