import { createReducer, createAction } from '@reduxjs/toolkit'

// Actions
export const logIn = createAction('logIn')
export const logOut = createAction('logOut')
export const setUserData = createAction('setUserData')
export const updateUserData = createAction('updateUserData')

// Initial state
const initialState = {
    name: "userReducer",
    connected: false,
    JWTtoken: null,
    userData: {},
}

// Reducer
const userReducer = createReducer(initialState, (builder) => {
    builder
        .addCase(logIn, (draft, action) => { // Utilisez 'action' pour accéder aux données de l'action
            if (!draft.connected) {
                draft.connected = true
                draft.JWTtoken = action.payload // Utilisez 'action.payload' pour accéder aux données de l'action
            }
        })
        .addCase(logOut, (draft) => {
            if (draft.connected) {
                draft.connected = false
                draft.userData = {}
                draft.JWTtoken = null
            }
        })
        .addCase(setUserData, (draft, action) => {
            draft.userData = action.payload
        })
        .addCase(updateUserData, (draft, action) => {
            draft.userData.firstName = action.payload.firstName
            draft.userData.lastName = action.payload.lastName
        })
})

export default userReducer
