// Importez la fonction configureStore depuis Redux Toolkit
import { configureStore } from "@reduxjs/toolkit";

// Importez votre reducer userReducer depuis son emplacement réel
import userReducer from "../utils/reducers";

// Créez le store Redux en utilisant configureStore
const store = configureStore({
  reducer: {
    // Associez le reducer userReducer au champ 'user' du store
    user: userReducer,
  },
});

// Exportez le store pour qu'il puisse être utilisé dans d'autres parties de votre application
export default store;
