// Sélectionne l'état de connexion de l'utilisateur
export const selectUserLogin = (state) => state.user.connected;

// Sélectionne le JSON Web Token (JWT) de l'utilisateur
export const selectJWT = (state) => state.user.JWTtoken;

// Sélectionne les données de l'utilisateur
export const selectUserData = (state) => state.user.userData;

// Sélectionne le prénom de l'utilisateur à partir des données de l'utilisateur
export const selectFirstName = (state) => {
  const userData = selectUserData(state);
  return userData ? userData.firstName : '';
};

// Sélectionne le nom de l'utilisateur à partir des données de l'utilisateur
export const selectLastName = (state) => {
  const userData = selectUserData(state);
  return userData ? userData.lastName : '';
};
