import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

// Fonction utilitaire pour effectuer une requête HTTP
async function fetchUserData({ email, password }) {
  const response = await fetch('http://localhost:3001/api/v1/user/login', {
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ email, password }),
  });

  if (response.status === 200) {
    const data = await response.json();
    return data.body;
  } else {
    const errorData = await response.json();
    throw new Error(errorData.message);
  }
}

export const loginUser = createAsyncThunk('users/login', async ({ email, password, remember }, thunkAPI) => {
  try {
    // Appel de la fonction utilitaire pour obtenir les données utilisateur
    const userData = await fetchUserData({ email, password });

    // Stockage du token d'authentification dans le stockage local
    localStorage.setItem('token', userData.token);

    // Gestion de la mémorisation de l'email et du mot de passe
    if (remember) {
      localStorage.setItem('email', email);
      localStorage.setItem('psw', password);
    } else {
      localStorage.removeItem('email');
      localStorage.removeItem('psw');
    }

    // Retour des données utilisateur
    return userData;
  } catch (error) {
    // Gestion des erreurs et rejet de la valeur avec un message d'erreur
    console.error('Error', error.message);
    return thunkAPI.rejectWithValue(error.message);
  }
});



// Fonction utilitaire pour effectuer une requête HTTP avec le jeton d'authentification
async function makeAuthenticatedApiRequest(url, method, token, body = null) {
  const headers = {
    Accept: 'application/json',
    Authorization: `Bearer ${token}`,
    'Content-Type': 'application/json',
  };

  const options = {
    method,
    headers,
    body: body ? JSON.stringify(body) : null,
  };

  const response = await fetch(url, options);

  if (response.status === 200) {
    const data = await response.json();
    return data;
  } else {
    const errorData = await response.json();
    throw new Error(errorData.message);
  }
}

export const fetchUserBytoken = createAsyncThunk('users/fetchUserByToken', async ({ token }, thunkAPI) => {
  try {
    // Appel de la fonction utilitaire pour obtenir les données utilisateur
    const profileUrl = 'http://localhost:3001/api/v1/user/profile';
    const userData = await makeAuthenticatedApiRequest(profileUrl, 'POST', token);

    // Retour des données utilisateur
    return userData;
  } catch (error) {
    // Gestion des erreurs et rejet de la valeur avec un message d'erreur
    console.error('Error', error.message);
    return thunkAPI.rejectWithValue(error.message);
  }
});

export const updateUserBytoken = createAsyncThunk('users/updateUserByToken', async ({ userName }, thunkAPI) => {
  try {
    // Récupération du jeton d'authentification du stockage local
    const token = localStorage.getItem('token');

    // Appel de la fonction utilitaire pour mettre à jour les informations utilisateur
    const profileUrl = 'http://localhost:3001/api/v1/user/profile';
    const updatedUserData = await makeAuthenticatedApiRequest(profileUrl, 'PUT', token, {
      userName,
    });

    // Retour des données utilisateur mises à jour
    return updatedUserData;
  } catch (error) {
    // Gestion des erreurs et rejet de la valeur avec un message d'erreur
    console.error('Error', error.message);
    return thunkAPI.rejectWithValue(error.message);
  }
});




// Crée une action asynchrone (thunk) pour récupérer des données de compte
export const fetchAccount = createAsyncThunk(
  'users/fetchAccount', // Nom de l'action
  async (thunkAPI) => {
    try {
      // Effectue une requête pour obtenir des données depuis un fichier JSON (simulation)
      const response = await fetch('/Data.json'); // Remplacez '/Data.json' par votre URL réelle
      const data = await response.json();

      // Vérifie le statut de la réponse HTTP
      if (response.status === 200) {
        // Si le statut est OK (200), retourne les données récupérées
        return data;
      } else {
        // Si le statut n'est pas OK, cela signifie une erreur
        // Rejette la promesse avec les données reçues, indiquant une erreur
        return thunkAPI.rejectWithValue(data);
      }
    } catch (e) {
      // En cas d'erreur lors de la requête
      console.error('Error', e.response.data); // Affiche l'erreur dans la console (simulée)
      // Rejette la promesse avec les données de l'erreur, indiquant une erreur
      return thunkAPI.rejectWithValue(e.response.data);
    }
  }
);

// Crée une tranche (slice) pour gérer l'état de l'utilisateur
export const userSlice = createSlice({
  name: 'user',
  initialState: {
    username: '',
    email: '',
    isFetching: false,
    isSuccess: false,
    isError: false,
    error: null,
  },
  reducers: {
    // Réducteur pour réinitialiser l'état
    clearState: (state) => {
      state.username = '';
      state.email = '';
      state.isFetching = false;
      state.isSuccess = false;
      state.isError = false;
      state.error = null;

      return state;
    },
  },
  extraReducers: {
    // Réducteurs supplémentaires gérant les actions asynchrones

    // Réducteur en cas de réussite de l'action loginUser
    [loginUser.fulfilled]: (state, { payload }) => {
      state.isFetching = false;
      state.isSuccess = true;
      state.username = payload.username;
      state.email = payload.email;

      return state;
    },
    // Réducteur en cas d'échec de l'action loginUser
    [loginUser.rejected]: (state, { payload }) => {
      state.isFetching = false;
      state.isError = true;
      state.error = payload.error;

      return state;
    },
    // Réducteur en cours d'exécution de l'action loginUser
    [loginUser.pending]: (state) => {
      state.isFetching = true;

      return state;
    },

    // Réducteurs supplémentaires pour d'autres actions asynchrones (fetchUserBytoken, updateUserBytoken, fetchAccount)

    // Réducteur en cas de réussite de l'action fetchAccount
    [fetchAccount.fulfilled]: (state, { payload }) => {
      state.isFetching = false;
      state.isSuccess = true;
      state.account = payload;

      return state;
    },
    // Réducteur en cas d'échec de l'action fetchAccount
    [fetchAccount.rejected]: (state) => {
      state.isFetching = false;
      state.isError = true;
      state.error = payload.error;

      return state;
    },
  },
});

// Exporte l'action clearState
export const { clearState } = userSlice.actions;

// Sélecteur pour accéder à l'état de l'utilisateur dans le store Redux
export const userSelector = (state) => state.user;
